import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "./IBuybackVault.sol";
import "./../dispatcher/IDispatcher.sol";
import "./../../release/core/fund/vault/IVault.sol";
import "./../../release/core/fund/comptroller/IComptroller.sol";

contract BuybackVaultV1 is IBuybackVault {
    using SafeMath for uint256;
    using SafeERC20 for ERC20;

    address private FEE_MANAGER;
    address private OWNER;
    address private DISPATCHER;

    address[] private trackedVaults;
    mapping(address => bool) private trackedVaultBoolHashmap;

    event OperatorsSet(address dispatcher, address feeManager);

    modifier onlyOwner {
        require(msg.sender == getOwner(), "Unauthorized");
        _;
    }

    modifier onlyOperator {
        require(msg.sender == getOwner() || msg.sender == FEE_MANAGER, "Unauthorized");
        _;
    }

    constructor() public {
        OWNER = msg.sender;
    }

    function setOperators(address _dispatcher, address _feeManager) external override onlyOwner {
        DISPATCHER = _dispatcher;
        FEE_MANAGER = _feeManager;

        emit OperatorsSet(_dispatcher, _feeManager);
    }

    function redeemShares(address[] memory _vaults) public override onlyOwner {
        for (uint256 i = 0; i < _vaults.length; i++) {
            uint256 _bal = ERC20(_vaults[i]).balanceOf(address(this));
            address _comptroller = IVault(_vaults[i]).getAccessor();
            if (_bal > 0) {
                IComptroller(_comptroller).redeemShares();
            }
        }
    }

    function redeemAllShares() external override onlyOwner {
        redeemShares(trackedVaults);
    }

    function withdrawERC20(address[] calldata _assets, address _withdrawAddress) external override onlyOwner {
        for(uint256 i = 0; i < _assets.length; i++) {
            uint256 _bal = ERC20(_assets[i]).balanceOf(address(this));
            ERC20(_assets[i]).transfer(_withdrawAddress, _bal);
        }
    }

    function addTrackedVault(address _vault) external override onlyOperator {
        if(!trackedVaultBoolHashmap[_vault]) {
            trackedVaultBoolHashmap[_vault] = true;
            trackedVaults.push(_vault);
        }
    }

    function removeTrackedVault(address _vault) external override onlyOperator {
        if(trackedVaultBoolHashmap[_vault]) {
            trackedVaultBoolHashmap[_vault] = false;
            uint256 _length = trackedVaults.length;
            for (uint256 i = 0; i < _length; i++) {
                if (trackedVaults[i] == _vault) {
                    if (i < _length - 1) {
                        trackedVaults[i] = trackedVaults[_length - 1];
                    }
                    trackedVaults.pop();
                    break;
                }
            }
        }
    }

    function getOwner() public view override returns (address) {
        if (DISPATCHER == address(0)) {
            return OWNER;
        }

        return IDispatcher(DISPATCHER).getOwner();
    }

    function getFeeManager() external view override returns (address) {
        return FEE_MANAGER;
    }

    function getDispatcher() external view override returns (address) {
        return DISPATCHER;
    }

    function isTrackedVault(address _vault) external view override returns (bool) {
        return trackedVaultBoolHashmap[_vault];
    }

    function getTrackedVaultsLength() external view override returns (uint256) {
        return trackedVaults.length;
    }

    function getTrackedVault(uint256 _id) external view override returns (address) {
        return trackedVaults[_id];
    }
}","keccak256":"0x78fcfb4839d7b32be6f6b392eb028e9032b44ec7394d8c0d6deea706695fecf2"},"contracts/persistent/buyback-vault/IBuybackVault.sol":{"content":"pragma solidity 0.6.12;

