pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

/// @title FeeManager Interface
/// @author Enzyme Council <security@enzyme.finance>
/// @notice Interface for the FeeManager
interface IFeeManager {
    // No fees for the current release are implemented post-redeemShares
    enum FeeHook {
        Continuous,
        BuySharesSetup,
        PreBuyShares,
        PostBuyShares,
        BuySharesCompleted,
        PreRedeemShares
    }
    enum SettlementType {None, Direct, Mint, Burn, MintSharesOutstanding, BurnSharesOutstanding, MintBuyback}

    function invokeHook(
        FeeHook,
        bytes calldata,
        uint256
    ) external;

    function invokeHookProtocolFee(
        FeeHook,
        bytes calldata,
        uint256
    ) external returns (bool, uint256);

    function updateProtocolFee(address) external;

    function addUserFeeWhitelist(address[] calldata fees, address user) external;

    function removeUserFeeWhitelist(address[] calldata fees, address user) external;
}
