import "./utils/FeeBase.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "../../utils/FundDeployerOwnerMixin.sol";
import "../../../../persistent/dao-staking/IDaoStaking.sol";

contract ProtocolFee is FeeBase, FundDeployerOwnerMixin {
    using SafeMath for uint256;

    address private STAKING_DAO;

    event StakingDaoUpdated(address indexed newStakingDao);

    event Settled(address indexed comptrollerProxy, address indexed payer, uint256 sharesQuantity);

    constructor(
    address _feeManager,
    address _fundDeployer,
    address _stakingDao)
    public FeeBase(_feeManager) FundDeployerOwnerMixin(_fundDeployer) {
        STAKING_DAO = _stakingDao;
    }

    function addFundSettings(address _comptrollerProxy, bytes calldata _settingsData)
        external
        override
        onlyFeeManager
    {
        return;
    }

    function implementedHooks()
        external
        view
        override
        returns (
            IFeeManager.FeeHook[] memory implementedHooksForSettle_,
            IFeeManager.FeeHook[] memory implementedHooksForUpdate_,
            bool usesGavOnSettle_,
            bool usesGavOnUpdate_
        )
    {
        implementedHooksForSettle_ = new IFeeManager.FeeHook[](1);
        implementedHooksForSettle_[0] = IFeeManager.FeeHook.PreRedeemShares;

        return (implementedHooksForSettle_, new IFeeManager.FeeHook[](0), false, false);
    }

    function settle(
        address _comptrollerProxy,
        address _vaultProxy,
        IFeeManager.FeeHook,
        bytes calldata _settlementData,
        uint256
    )
        external
        override
        onlyFeeManager
        returns (
            IFeeManager.SettlementType settlementType_,
            address payer_,
            uint256 sharesDue_
        )
    {

        uint256 redeemSharesBeforeFee;
        (payer_, redeemSharesBeforeFee) = __decodePreRedeemSharesSettlementData(_settlementData);

        if (STAKING_DAO == address(0) || payerWhitelist[payer_]) {
            return (IFeeManager.SettlementType.None, address(0), 0);
        }

        uint256 rate = IDaoStaking(STAKING_DAO).getExitFee(_vaultProxy, payer_);
        uint256 rateDiv = IDaoStaking(STAKING_DAO).getFeeDivisor();
        sharesDue_ = redeemSharesBeforeFee.mul(rate).div(rateDiv);

        if (sharesDue_ == 0) {
            return (IFeeManager.SettlementType.None, address(0), 0);
        }

        emit Settled(_comptrollerProxy, payer_, sharesDue_);

        return (IFeeManager.SettlementType.MintBuyback, payer_, sharesDue_);
    }

    function identifier() external pure override returns (string memory identifier_) {
        return "PROTOCOL_FEE";
    }

    function getStakingDao() external view returns (address) {
        return STAKING_DAO;
    }

    function updateStakingDao(address _newStaking) external onlyFundDeployerOwner returns (address) {
        STAKING_DAO = _newStaking;

        emit StakingDaoUpdated(_newStaking);
    }
}","keccak256":"0x4bd5a3f800b28dfd0aaaba338bc6cad649f3d8bc9a37bd53152662ac37237ea8"},"contracts/release/extensions/fee-manager/fees/utils/FeeBase.sol":{"content":"// SPDX-License-Identifier: GPL-3.0

